<?php // no direct access defined( '_JEXEC' ) or die( 'Restricted access' ); jimport( 'joomla.application.component.view'); class CartSaverViewCartSaver extends JView{    function display($tpl = null)    {		$model = &$this->getModel();        $newcart = $model->getVMCartContents();        $this->assignRef( 'newcart', $newcart );         parent::display($tpl);    }}